refentry.refsect1:
  &reftitle.examples;

   MongoDB\Driver\Manager::executeWriteBatch example


<?php
$batch = new MongoDB\Driver\WriteBatch();

$batch->insert(array("_id" => 1, "x" => 1));
$batch->insert(array("_id" => 2, "x" => 2));

$batch->update(array(  "x" => 2), array('$set' => array("x" => 1)), array("limit" => 1, "upsert" => false));
$batch->update(array(  "x" => 3), array('$set' => array("x" => 3)), array("limit" => 1, "upsert" => true));
$batch->update(array("_id" => 3), array('$set' => array("x" => 3)), array("limit" => 1, "upsert" => true));

$batch->insert(array("_id" => 4, "x" => 2));

$batch->delete(array("x" => 1), array("limit" => 1));


$manager = new MongoDB\Driver\Manager("mongodb://localhost:27017");
$writeConcern = new MongoDB\Driver\WriteConcern(MongoDB\Driver\WriteConcern::MAJORITY, 100);
$result = $manager->executeWriteBatch("databaseName.collectionName", $batch, $writeConcern);

printf("Inserted %d document(s)\n", $result->getInsertedCount());
printf("Matched  %d document(s)\n", $result->getMatchedCount());
printf("Updated  %d document(s)\n", $result->getModifiedCount());
printf("Upserted %d document(s)\n", $result->getUpsertedCount());
printf("Deleted  %d document(s)\n", $result->getDeletedCount());

foreach ($result->getUpsertedIds() as $index => $id) {
    printf("upsertedId[%d]: ", $index);
    var_dump($id);
}

/* If the WriteConcern could not be fulfilled */
if ($writeConcernError = $result->getWriteConcernError()) {
    printf("%s (%d): %s\n", $writeConcernError->getMessage(), $writeConcernError->getCode(), var_export($writeConcernError->getInfo(), true));
}

/* If a write could not happen at all */
foreach ($result->getWriteErrors() as $writeError) {
    printf("Operation#%d: %s (%d)\n", $writeError->getIndex(), $writeError->getMessage(), $writeError->getCode());
}
?>


